#!/bin/shecho installing TNT4J and JESL libraries...mvn install:install-file -Dfile="${BASH_SOURCE%/*}/tnt4j-api.jar" -DgroupId=com.nastel.jkool.tnt4j -DartifactId=tnt4j-api -Dversion=1.0.0 -Dpackaging=jarmvn install:install-file -Dfile="${BASH_SOURCE%/*}/tnt4j-api-utils.jar" -DgroupId=com.nastel.jkool.tnt4j -DartifactId=tnt4j-api-utils -Dversion=1.0.0 -Dpackaging=jarmvn install:install-file -Dfile="${BASH_SOURCE%/*}/jkool-jesl.jar" -DgroupId=com.nastel.jkool.tnt4j -DartifactId=jkool-jesl -Dversion=1.0.0 -Dpackaging=jarecho installing TNT4J logger libraries...mvn install:install-file -Dfile="${BASH_SOURCE%/*}/tnt4j-log4j12.jar" -DgroupId=com.nastel.jkool.tnt4j -DartifactId=tnt4j-log4j12 -Dversion=1.0.0 -Dpackaging=jar#mvn install:install-file -Dfile="${BASH_SOURCE%/*}/tnt4j-logback.jar" -DgroupId=com.nastel.jkool.tnt4j -DartifactId=tnt4j-logback -Dversion=1.0.0 -Dpackaging=jarecho DONE!#read -p "Press [Enter] key to exit..."