#!/bin/shecho installing TNT4J and JESL libraries...mvn install:install-file -Dfile="${BASH_SOURCE%/*}/tnt4j-api.jar" -DgroupId=com.jkoolcloud -DartifactId=tnt4j -Dversion=2.2 -Dpackaging=jarmvn install:install-file -Dfile="${BASH_SOURCE%/*}/jkool-jesl.jar" -DgroupId=com.jkoolcloud -DartifactId=jesl -Dversion=0.1.5 -Dpackaging=jarecho installing TNT4J logger libraries...mvn install:install-file -Dfile="${BASH_SOURCE%/*}/tnt4j-log4j12.jar" -DgroupId=com.jkoolcloud.tnt4j.logger -DartifactId=log4j -Dversion=0.1 -Dpackaging=jar#mvn install:install-file -Dfile="${BASH_SOURCE%/*}/tnt4j-logback.jar" -DgroupId=com.jkoolcloud.tnt4j.logger -DartifactId=logback -Dversion=0.1 -Dpackaging=jarecho DONE!#read -p "Press [Enter] key to exit..."